custom-sql.sql:
SELECT docfileid as docfileid , filenumber as filenumber , subject as subject , categoryvalue as category , remarks as remark , createDate as createdOn  
	FROM jet_process_docfile  INNER JOIN 
	md_category  ON categorydataid = categoryid where userpostid = ? ;

SELECT receiptid as receiptId , receiptnumber as receiptnumber , subject as subject , categoryvalue as category , createDate as createDate ,  remarks as remark , viewpdfurl
	FROM jet_process_receipt INNER JOIN 
	md_category  ON categorydataid = receiptcategoryid where userpostid = ? ;
