custom-sql.sql:
SELECT filenumber as filenumber , subject as subject , categoryvalue as category , remarks as remark , createDate as createdOn  
	FROM jet_process_docfile  INNER JOIN 
	category  ON categorydataid = categoryid where userpostid = ? ;

SELECT receiptnumber as receiptnumber , subject as subject , categoryvalue as category , createDate as createDate ,  remarks as remark
	FROM jet_process_receipt INNER JOIN 
	category  ON categorydataid = receiptcategoryid where userpostid = ? ;
